import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Admission = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    course: '',
    college: '',
    category: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    console.log('Form submitted:', formData);
    alert('Application submitted successfully! We will contact you soon.');
  };

  const requirements = [
    "Caste certificate (SC category)",
    "Aadhaar Card and Samagra ID",
    "Academic marksheets (Class X and XII)",
    "Parents' income certificate",
    "Photographs (applicant, parents, local guardian)",
    "Migration certificate (if from out of state)"
  ];

  const fees = [
    { type: "Single Room", amount: "₹8,000", period: "per month" },
    { type: "Double Sharing", amount: "₹5,500", period: "per month" },
    { type: "Triple Sharing", amount: "₹4,000", period: "per month" },
    { type: "Security Deposit", amount: "₹5,000", period: "one-time" }
  ];

  return (
    <section id="admission" className="py-20 bg-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-4">
            Admission <span className="text-primary-600">Process</span>
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Complete guide for SC students seeking hostel accommodation in Madhya Pradesh
          </p>
        </motion.div>

        {/* Eligibility Criteria */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="mb-16"
        >
          <h3 className="text-3xl font-bold text-gray-900 mb-8 text-center">Eligibility Criteria</h3>
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="card p-6 text-center">
              <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-primary-600 text-xl">🏠</span>
              </div>
              <h4 className="text-lg font-semibold text-gray-900 mb-2">Native of MP</h4>
              <p className="text-gray-600 text-sm">Must be a resident of Madhya Pradesh</p>
            </div>
            <div className="card p-6 text-center">
              <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-primary-600 text-xl">📋</span>
              </div>
              <h4 className="text-lg font-semibold text-gray-900 mb-2">SC Category</h4>
              <p className="text-gray-600 text-sm">Must belong to Scheduled Caste (SC) category</p>
            </div>
            <div className="card p-6 text-center">
              <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-primary-600 text-xl">🎓</span>
              </div>
              <h4 className="text-lg font-semibold text-gray-900 mb-2">Student Status</h4>
              <p className="text-gray-600 text-sm">Class 1-12 or college level student</p>
            </div>
            <div className="card p-6 text-center">
              <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-primary-600 text-xl">📍</span>
              </div>
              <h4 className="text-lg font-semibold text-gray-900 mb-2">Distance</h4>
              <p className="text-gray-600 text-sm">Significant distance from school/college</p>
            </div>
          </div>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-12 mb-16">
          {/* Requirements & Fees */}
          <div className="space-y-8">
            <motion.div
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="card p-6"
            >
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Required Documents</h3>
              <ul className="space-y-3">
                {requirements.map((req, index) => (
                  <li key={index} className="flex items-center">
                    <span className="w-2 h-2 bg-primary-500 rounded-full mr-3"></span>
                    <span className="text-gray-700">{req}</span>
                  </li>
                ))}
              </ul>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              viewport={{ once: true }}
              className="card p-6"
            >
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Fee Structure</h3>
              <div className="space-y-4">
                {fees.map((fee, index) => (
                  <div key={index} className="flex justify-between items-center py-2 border-b border-gray-100 last:border-b-0">
                    <span className="text-gray-700">{fee.type}</span>
                    <div className="text-right">
                      <span className="text-lg font-semibold text-primary-600">{fee.amount}</span>
                      <span className="text-sm text-gray-500 block">{fee.period}</span>
                    </div>
                  </div>
                ))}
              </div>
            </motion.div>
          </div>

          {/* Application Form */}
          <motion.div
            initial={{ opacity: 0, x: 30 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="card p-6"
          >
            <h3 className="text-2xl font-bold text-gray-900 mb-6">Apply Now</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid md:grid-cols-2 gap-4">
                <input
                  type="text"
                  name="name"
                  placeholder="Full Name"
                  value={formData.name}
                  onChange={handleChange}
                  className="input-field"
                  required
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email Address"
                  value={formData.email}
                  onChange={handleChange}
                  className="input-field"
                  required
                />
              </div>
              <div className="grid md:grid-cols-2 gap-4">
                <input
                  type="tel"
                  name="phone"
                  placeholder="Phone Number"
                  value={formData.phone}
                  onChange={handleChange}
                  className="input-field"
                  required
                />
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  className="input-field"
                  required
                >
                  <option value="">Select Category</option>
                  <option value="SC">SC</option>
                  <option value="ST">ST</option>
                  <option value="OBC">OBC</option>
                </select>
              </div>
              <input
                type="text"
                name="course"
                placeholder="Course/Program"
                value={formData.course}
                onChange={handleChange}
                className="input-field"
                required
              />
              <input
                type="text"
                name="college"
                placeholder="College/University"
                value={formData.college}
                onChange={handleChange}
                className="input-field"
                required
              />
              <textarea
                name="message"
                placeholder="Additional Message (Optional)"
                value={formData.message}
                onChange={handleChange}
                rows="4"
                className="input-field"
              ></textarea>
              <motion.button
                type="submit"
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                className="btn-primary w-full"
              >
                Submit Application
              </motion.button>
            </form>
          </motion.div>
        </div>

        {/* Process Steps */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center"
        >
          <h3 className="text-3xl font-bold text-gray-900 mb-12">Admission Process</h3>
          <div className="grid md:grid-cols-4 gap-8">
            {[
              { step: "1", title: "Apply Online", desc: "Fill the application form" },
              { step: "2", title: "Document Review", desc: "We verify your documents" },
              { step: "3", title: "Interview", desc: "Brief interaction with our team" },
              { step: "4", title: "Admission", desc: "Welcome to SC Boys Hostel!" }
            ].map((item, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="text-center"
              >
                <div className="w-16 h-16 bg-gradient-to-br from-primary-500 to-primary-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-white font-bold text-xl">{item.step}</span>
                </div>
                <h4 className="text-lg font-semibold text-gray-900 mb-2">{item.title}</h4>
                <p className="text-gray-600">{item.desc}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Admission;
